var index=document.querySelector("#index");if(index){var link=document.querySelector(".link-map"),popup=document.querySelector(".modal-write-us"),close=popup.querySelector(".button-close-window"),login=popup.querySelector("[name=name]"),email=popup.querySelector("[name=email]"),textletter=popup.querySelector("[name=textletter]"),form=popup.querySelector("form");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),login.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show")}),form.addEventListener("submit",function(e){login.value&&email.value&&textletter.value||(e.preventDefault(),console.log("Нужно ввести ваше имя, e-mail и текст письма"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("modal-show")&&popup.classList.remove("modal-show"))});var mapLink=document.querySelector(".modal-map"),mapPopup=document.querySelector(".modal-map-popup"),mapClose=mapPopup.querySelector(".button-close-window");mapLink.addEventListener("click",function(e){e.preventDefault(),mapPopup.classList.add("modal-show")}),mapClose.addEventListener("click",function(e){e.preventDefault(),mapPopup.classList.remove("modal-show")})}for(var buyLink=document.querySelectorAll(".buy"),buyPopup=document.querySelector(".modal-add-goods"),buyClose=buyPopup.querySelector(".button-close-window"),i=0;i<buyLink.length;i++)buyLink[i].addEventListener("click",function(e){e.preventDefault(),buyPopup.classList.add("modal-show")});buyClose.addEventListener("click",function(e){e.preventDefault(),buyPopup.classList.remove("modal-show")});